
public class ex6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 125;
		int b = 24;
		System.out.println("a = 125");
		System.out.println("b = 24");
		System.out.println("125 + 24 = " + (a + b));
		System.out.println("125 - 24 = " + (a - b));
		System.out.println("125 * 24 = " + (a * b));
		System.out.println("125 / 24 = " + (a / b));
		System.out.println("125 % 24 = " + (a % b));

	}

}
